INSERT INTO `questions` (`id`, `subject`, `level`, `question`, `answer`, `choice1`, `choice2`, `choice3`, `choice4`) VALUES(124981, 'mathematics', 'Junior high School', 'ด.ช.ณัฐ สังเกตส่วนยอดและส่วนฐานของตึกแห่งหนึ่งจากลิฟต์แก้วตัวหนึ่งที่กำลังเคลื่อนที่ โดยพบว่า ขณะที่มองเห็นยอดตึกเป็นมุมก้ม \\(15^o\\) จะมองเห็นฐานตึกเป็นมุมก้ม \\(\\theta \\) และขณะที่มองเห็นฐานตึกเป็นมุมก้ม \\(60^o\\) จะเห็นยอดตึกเป็นมุมก้ม \\(\\theta \\)\n\nจงหาว่า \\(\\cos \\theta \\) มีค่าเท่ากับข้อใดต่อไปนี้ (กำหนดให้ \\(\\cos {{15}^{o}}=\\dfrac{\\sqrt{3}+1}{2\\sqrt{2}}\\) )', 2, '\\(\\sqrt{\\dfrac{2}{3}}\\)', '\\(\\dfrac{1}{\\sqrt{2}}\\)', '\\(\\dfrac{2}{3}\\)', '\\(\\dfrac{1}{\\sqrt{3}}\\)');
